// Parse the AI response to extract LinkedIn and Twitter posts
const aiResponse = $input.first().json.output;

try {
  // Try to parse as JSON directly
  let parsed;

  // Check if response contains JSON in markdown code block
  const jsonMatch = aiResponse.match(/```json?\s*([\s\S]*?)```/);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[1].trim());
  } else {
    // Try direct JSON parse
    parsed = JSON.parse(aiResponse);
  }

  return {
    json: {
      linkedin_post: parsed.linkedin_post || '',
      twitter_post: parsed.twitter_post || ''
    }
  };
} catch (e) {
  // Fallback: extract content manually
  return {
    json: {
      linkedin_post: aiResponse.substring(0, 500),
      twitter_post: aiResponse.substring(0, 280),
      parse_error: e.message
    }
  };
}

