parameters.jsonBody:
={
  "video_inputs": [
    {
      "character": {
        "type": "avatar",
        "avatar_id": "{{ $('Setup Heygen').item.json.avatar_id }}",
        "avatar_style": "normal",
        "scale": 1.0,
        "offset": {
          "x": 0.15,
          "y": 0.15
        },
        "matting": true
      },
      "voice": {
        "type": "text",
        "input_text": {{ $('AI Agent').item.json.output.toJsonString() }},
        "voice_id": "{{ $('Setup Heygen').item.json.voice_id }}",
        "speed": 1.1,
        "pitch": 50,
        "emotion": "Excited"
      },
      "background": {
        "type": "video",
        "url": "{{ $('Setup Heygen').item.json.background_video_url }}",
        "play_style": "loop",
        "fit": "cover"
      }
    }
  ],
  "dimension": {
    "width": 720,
    "height": 1280
  },
  "aspect_ratio": "9:16",
  "caption": false,
  "title": "{{ $('Write Short Caption').item.json.message.content }}"
}

